$(document).ready(function () {
    $('#back').height($(window).height()).width($(window).width()-320);
    $('#back').css({left: "160px"});
    String.prototype.replaceAt=function(index, character) {
        return this.substr(0, index) + character + this.substr(index+character.length);
    }
    $('#score').html('Score: '+score);
    $('#levstat').html("You are on Level "+lev+"<br /><br />");
    level(lev);
    $('#solve').on('click', 'a', function(e) {
        e.preventDefault();
        $('#solution').append($(this).clone());
        $(this).addClass('disabled');
    });
    $('#solution').on('click', 'a', function(e) {
        e.preventDefault();
        //$('#solve').append($(this).clone());
        $(this).remove();
        $('#'+$(this).attr('id')).removeClass('disabled');
    });
});
function level(x) {
    x = Math.min(3, x);
    var w = Math.floor(Math.random()*eval('wordlist'+x).length);
    if(lev>3) word = wordlist3[w];
    else word = eval('wordlist'+x+'[w]');
    solved = word;
    var idx = 0;
    var temp;
    while(word==solved) {
        for(var i=0;i<x+3;i++) {
            idx = Math.floor(i + Math.random()*(x+3-i));
            temp = word[i];
            word = word.replaceAt(i, word[idx]);
            word = word.replaceAt(idx, temp);
        }
    }
    $('#solve').html('');
    $('#solution').html('');
    for(var i=0;i<x+3;i++) {
        $('#solve').append('<a id="'+i+'" class="btn btn--l btn--blue lbtn add" href="#">&nbsp;'+word[i]+'&nbsp;</a>');
    }
}
function submitans() {
    var answer = "";
    $('#solution').children('a').each(function() {
        answer += $(this).html().split('&')[1].substring(5);
    });
    var y = lev>3?3:lev
    if (answer == solved || eval('wordlist' + y).indexOf(answer) != -1) {
        wordcount++;
        localStorage.wordcount = wordcount;
        if(lev==1)inc=10;
        else if(lev==2)inc=20;
        else if(lev==3||lev==4)inc=30;
        else if(lev==5)inc=40;
        score += inc;
        $('#status').html("Correct! "+"+"+inc+" points");
        $('#score').html('Score: '+score);

        if(score>=100 && score-inc<100) {
            lev = 2;
            $('#status').html('Level 2 reached!');
        }
        else if(score>=300 && score-inc<300) {
            lev = 3;
            $('#status').html('Level 3 reached!');
        }
        else if(score>=600 && score-inc<600) {
            lev = 4;
            $('#status').html('Level 4 reached!');
        }
        else if(score>=1000 && score-inc<1000) {
            lev = 5;
            $('#status').html('Level 5 reached!');
        }
        level(lev);
        if (wordcount == 1) { $('#status').html('"Fine Debut" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 25) { $('#status').html('"Word Kiddie" badge earned!'); badges++; localStorage.badges = badges;}
        else if (wordcount == 50) { $('#status').html('"Spell Bee" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 100) { $('#status').html('"Centurion" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 500) { $('#status').html('"Cardinal" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 1000) { $('#status').html('"Millenary" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 50000) { $('#status').html('"Auriferous" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 100000) { $('#status').html('"Word Wizard" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 250000) { $('#status').html('"Quartermaster" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 500000) { $('#status').html('"Clairvoyant" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 2500000) { $('#status').html('"In The Limelight" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 10000000) { $('#status').html('"Grandmaster" badge earned!'); badges++; localStorage.badges = badges; }
        else if (wordcount == 10) { $('#status').html('"Rosewood" found!'); artifacts++; localStorage.artifacts= artifacts; }
        else if (wordcount == 250) { $('#status').html('"Silver Spoon" found!'); artifacts++; localStorage.artifacts = artifacts; }
        else if (wordcount == 2500) { $('#status').html('"Golden Cookie" found!'); artifacts++; localStorage.artifacts = artifacts; }
        else if (wordcount == 5000) { $('#status').html('"Emerald Ring" found!'); artifacts++; localStorage.artifacts = artifacts; }
        else if (wordcount == 10000) { $('#status').html('"Ruby Morsel" found!'); artifacts++; localStorage.artifacts = artifacts; }
        else if (wordcount == 75000) { $('#status').html('"Alexandrite" found!'); artifacts++; localStorage.artifacts = artifacts; }
        else if (wordcount == 1000000) { $('#status').html('"Platinum Trophy" found!'); artifacts++; localStorage.artifacts = artifacts; }
        else if (wordcount == 5000000) { $('#status').html('"Diamond Statue" found!'); artifacts++; localStorage.artifacts = artifacts; }
    }
    else {
        if(lev==4) {
            score-=10;
            if(score<600){lev=3;level(lev);}
            $('#status').html("Incorrect! -10 points");
        }
        else if(lev==5) {
            score-=20;
            if(score<1000){lev=4;level(lev);}
            $('#status').html("Incorrect! -20 points");
        }
        else $('#status').html("Incorrect answer!");
        $('#solve').children('a').each(function () {
            $(this).removeClass('disabled');
        });
        $('#solution').html('');
        $('#score').html('Score: '+score);
    }
    localStorage.score = score;
    if (score > highscore) { highscore = score;localStorage.highscore = score; }
    localStorage.level = lev;
    if(lev==1)$('#levstat').html("You are on Level "+lev+": \"Spell Kiddie\"<br>"+(100-score)+" more points for level 2!");
    else if(lev==2)$('#levstat').html("You are on Level "+lev+": \"Novice\"<br>"+(300-score)+" more points for level 3!");
    else if(lev==3)$('#levstat').html("You are on Level "+lev+": \"Competent\"<br>"+(600-score)+" more points for level 4!");
    else if(lev==4)$('#levstat').html("You are on Level "+lev+": \"Proficient\"<br>"+(1000-score)+" more points for level 5!");
    else if(lev==5)$('#levstat').html("You are on Level "+lev+": \"Expert\"<br>You are God!");
}
function pass() {
    if (lev == 5) {
        score -= 10;
        localStorage.score = score;
        $('#score').html('Score: ' + score);
        $('#status').html("-10 points! It was " + solved.toUpperCase());
        if (score < 1000) { lev = 4 }
        if (lev == 4) $('#levstat').html("You are on Level " + lev + ": \"Proficient\"<br>" + (1000 - score) + " more points for level 5!");
        localStorage.level = lev;
        level(lev);
    }
    else {
        $('#status').html("It was <a style='color:#acf;' href='http://dictionary.reference.com/browse/"
            + solved + "'>" + solved.toUpperCase() + "</a>"); level(lev);
    }
}